windowreferencecontrol.script:
function onDragStart(button, x, y, dragdata)
	if super and super.onDragStart then
		super.onDragStart(button, x, y, dragdata);
	end
	Debug.chat(class);
	dragdata = dragdata.createBaseData();
	--dragdata.desc = "test";
	Debug.chat(dragdata);
end
function onDragEnd(dragdata)
	Debug.chat(dragdata);
end

smartstring.script:
function onUpdating(bUser, sPrevious, sNew)
	if not bUser then
		setValue(AncestryManager.getReplacement(sNew));
		return true;
	end
end

smartformattedtext.script:
function onUpdating(bUser, sPrevious, sNew)
	if not bUser then
		setValue(AncestryManager.getReplacement(sNew));
		return true;
	end
end
